mapper.select:
    select g.id,g.`name`,g.cateId,g.sub_cate_id subCateId,g.channel_type channelType,g.summary,IFNULL(SUM(s.spu_total_count),0) spuTotalCount,IFNULL(SUM(s.spu_count),0) spuCount FROM fm_goods g left JOIN fm_spu_stock s ON g.id = s.goods_id


       		and g.id = #{goodsId,jdbcType=BIGINT}


       		and g.cateId = #{cateId,jdbcType=BIGINT}


       		and g.sub_cate_id = #{subCateId,jdbcType=BIGINT}


       		and g.is_valid = #{isValid,jdbcType=INTEGER}


       		and g.channel_type = #{channelType,jdbcType=INTEGER}


       		and instr(g.name, #{name})


     GROUP BY g.id


	         limit #{offer}, #{pageSize}

select count(*) FROM fm_goods g 


   		and g.id = #{goodsId,jdbcType=BIGINT}


   		and g.cateId = #{cateId,jdbcType=BIGINT}


   		and g.sub_cate_id = #{subCateId,jdbcType=BIGINT}


   		and g.is_valid = #{isValid,jdbcType=INTEGER}


   		and g.channel_type = #{channelType,jdbcType=INTEGER}


   		and instr(g.name, #{name})
